init:
$('body').addClass('textured');$('.button').pretouch();if(OF.page.params.spotlight)$('section.footer_menu').hide();if(!OF.menu){OF.menu=function(buttonName){if(buttonName=='home')OF.menu.home();else if(buttonName=='settings')OF.menu.settings();else if(buttonName=='exit')OF.menu.exit();else{OF.log('Unknown menu button hit: '+buttonName);}};OF.menu.home=function(){if(OF.navigationStack[0].url.match(/dashboard\/user/)){OF.goBack({root:true});}else{OF.navigateToUrl("dashboard/user",{complete:function(){OF.pages.unshift(OF.pages.pop());OF.action('back',{root:true},OF.contentLoaded);}});}};OF.menu.settings=function(){OF.sendAction('openSettings');};OF.menu.exit=function(){OF.sendAction('dismiss');};}
if(!OF.friendsList){OF.friendsList={init:function(){$.lazyLoad({threshold:0.5,nextPageLoaded:function(page,data){if(page==1){OF.friendsList.renderFriends(data);}else{OF.friendsList.addFriends(data);}},nextPageShouldLoad:function(page,callback){OF.api.request('/xp/friends',{params:{user_id:OF.page.user.id,page:page,page_size:50},success:function(data){callback(data.users,data.users.length<50)},failure:function(){callback([]);},complete:function(){$("#loading").hide();}});},isEmpty:function(){OF.friendsList.renderFriends([]);},isFinished:function(){$("#loading").hide();}});},renderFriendItems:function(friends){var html=$.map(friends,function(friend){return tmpl('user_tmpl',friend);});return html;},renderFriends:function(friends){var html=OF.friendsList.renderFriendItems(friends);if(html.length>0){$('#friends_list > ul').html(html.join('')).unhide();$("#friends_list .no_data").hide();}else{$('#friends_list > ul').hide();$("#friends_list .no_data").unhide();}},addFriends:function(friends){var html=OF.friendsList.renderFriendItems(friends);$('#friends_list > ul').append(html.join(''));}};}
$('#home_button').touch(OF.menu.home);var friendables=new Array();var users_count=0;var PENDING_LABEL='请求已发送';$('.add_friend_button').touch(function(){var friend_id=this.id;$(this).addClass('disabled');$(this).text(PENDING_LABEL);OF.api.request('/xp/addrr_friends',{background:true,method:'POST',params:{friend_id:friend_id},loader:'#loading .loading',success:function(data){var index_to_remove=friendables.indexOf(Number(friend_id));if(index_to_remove!=-1)friendables.splice(index_to_remove,1);if((friendables.length<1)&&(users_count>1)){$('#add_all_friends_button').addClass('disabled').val(PENDING_LABEL);}}});});$('#add_all_friends_button').touch(function(){$(this).hide();$('.add_friend_button').addClass('disabled').text(PENDING_LABEL);OF.api.request('/xp/addrr_friends',{method:'POST',params:{friend_ids:friendables},loader:'#loading .loading',success:function(data){$('#find_friends').addClass('hidden');$('#all_requests_sent').removeClass('hidden');$('#submit').addClass('hidden');}});});$('#submit').touch(function(){OF.api.request('/xp/rrlogout',{method:'PUT',loader:'#submit',success:function(){OF.goBack();}});});OF.api.request('/xp/rrlist',{loader:'#loading .loading',success:function(data){var userTmpl=tmpl('a_rr_user');var html=[];users_count=data.users.length;if(users_count>0){$.each(data.users,function(){html.push(userTmpl(this));friendables.push(this.id);});$('ul#friends_list').append(html.join(""));$('#rr_friend_count_bar').unhide();if(users_count<2){}}else{$('#no_friends_found').unhide();}},failure:function(data,status){$('#no_friends_found').unhide();}});
