init:
$('body').addClass('textured');$('#header .loading').hide();$('.button').pretouch();if(OF.page.params.mode=='switch'){OF.introSwitch=true;}
if(OF.introSwitch){$('.switch_flow').unhide(0);$('.intro_flow').hide();}
$('#mobile_form_submit').touch(function(){if(!$('form').isLoading()){$('form').data('isLoading',true);var mobile=$.trim($('#user_mobile_number').val());if(mobile.length==0||!mobile.match(/^(134|135|136|137|138|139|150|151|152|157|158|159|187|188|182|130|131|132|155|156|185|186|133||153|180|189)(\d){8}$/)){$('form').data('isLoading',false);OF.alert("提示","请确认手机号码填写是否正确");return false;}
OF.loader.show();$("#mobile_form_submit").html("正在找回密码...");OF.api.request('/xp/users/reset_password',{method:'PUT',httpParams:{timeout:100000},params:{mobile_number:mobile},loader:'#submit',success:function(){OF.goBack();OF.alert("提示","您的密码已重置并发送至您的手机上，请尽快进行密码修改");},failure:function(data){OF.loader.hide();$('form').data('isLoading',false);$("#mobile_form_submit").html("使用手机号找回密码");OF.alert("错误提示",data.exception.message);}});}});$('#email_form_submit').touch(function(){var email=$.trim($('#user_email').val());if(email.length==0||!email.match(/.+@.+/)){OF.alert(_('Invalid Email'),_('Enter your email to retrieve your Feint account password'));return;}
OF.api.request('/xp/users/reset_password',{method:'PUT',params:{email:email},loader:'#submit',success:function(){OF.goBack();OF.alert(_('Success'),_('Your password has been reset and emailed to you.'));},failure:function(data){OF.alert("错误提示",data.exception.message);}});});
