init:
$('body').addClass('textured');$('#header .loading').hide();$('.button').pretouch();if(OF.page.params.mode=='switch'){OF.introSwitch=true;}
if(OF.introSwitch){$('.switch_flow').unhide(0);$('.intro_flow').hide();}
if(OF.user.id){isLogin=true;}else{isLogin=false;}
if(OF.users.length>1&&OF.page.params.user_id){$.each(OF.users,function(){if(this.user.id==OF.page.params.user_id){OF.page.user=this.user;}});}
$("#new_account").touch(function(){if(OF.page.params.mode){OF.navigateToUrl("intro/index",{params:{for_new_account:"true",mode:"switch",spotlight:OF.page.params.spotlight}});}else{OF.navigateToUrl("intro/index",{params:{for_new_account:"true",spotlight:OF.page.params.spotlight}});}});$("#another_account").touch(function(){if(OF.page.params.mode){OF.navigateToUrl("intro/login",{params:{mode:"switch",spotlight:OF.page.params.spotlight}});}else{OF.navigateToUrl("intro/login",{params:{spotlight:OF.page.params.spotlight}});}});if(OF.page.params.spotlight){$('#submit').text('去九城游戏中心');}
if(OF.page.params.mode){$('#submit').text('去九城游戏中心');}
$('.login_reminder').html(_('Log in to The9 Game Zone to start playing %1 with your friends and other gamers!',OF.game.name));$('.user_name').html(OF.page.user.name);$('.experience_level').html('Lv'+OF.page.user.experience_level);$('#gem_icon img').attr('src','images/icon.brisks.level.'+OF.page.user.experience_level+'.png');$('.gamerscore_val').html(OF.page.user.gamer_experience);if(OF.page.user.profile_picture_url){$('.avatar').css('background-image','url('+OF.page.user.profile_picture_url+')');}
OF.page.boostrapCallback=function(status,response){$('form').data('isLoading',false);OF.loader.hide();if(OF.page.params.mode){$('#submit').html('去九城游戏中心');}else{$('#submit').html(_('Start Playing!'));}
if(status.match(/^2/)){if(isLogin){OF.user=OF.page.user;if(OF.sdkSupportThe9Download){OF.push('dashboard/user');}else{OF.sendAction('dashboard');}}else{OF.sendAction('dismiss');}}else if(status.match(/404/)){OF.alert(_('Account not found'),"该手机号，邮箱或用户名不存在");}else if(status.match(/401/)){OF.alert(_('Incorrect Password'),_('The password entered is incorrect.'));}else if(status.match(/8080/)){OF.sendAction("openSpecifyDashboardScreen",OF.page.params);}else{var message=(response.exception&&response.exception.message)?$.htmlDecode(response.exception.message):_('Check your username and password and try again');OF.alert(_('Authentication failed'),message);}};$('#submit').touch(function(){var need_redirect="false";if(OF.page.params.redirect_screen){need_redirect="true"}
if(!$('form').data('isLoading')){$('form').data('isLoading',true);$("#submit").html(_('Loading')).append('&hellip;');OF.loader.show();OF.sendAction('loginUser',{user_id:OF.page.user.id,callback:'OF.page.boostrapCallback',need_redirect:need_redirect});}});$('form').submit(function(){OF.touch('#submit');return false;});
