init:
$('body').addClass('textured');$('#header .loading').hide();$('.button').pretouch();if(OF.page.params.mode=='switch'){OF.introSwitch=true;}
if(OF.introSwitch){$('.switch_flow').unhide(0);$('.intro_flow').hide();}
if(OF.user.id){isLogin=true;}else{isLogin=false;}
$('.login_reminder').html(_('Log in to The9 Game Zone to start playing %1 with your friends and other gamers!',OF.game.name));if(OF.page.params.spotlight){$('#submit').text('去九城游戏中心');}
if(OF.page.params.mode){$("#submit").text("去九城游戏中心");}
$('.game_name').html(OF.game.name);if(OF.spotlight){$('.intro_flow').hide();$('.intro_flow_spotlight').unhide();}
OF.page.boostrapCallback=function(status,response){$('form').isLoading(false);OF.loader.hide();if(OF.page.params.mode){$('#submit').html('去九城游戏中心');}else{$('#submit').html(_('Start Playing!'));}
if(status.match(/^2/)){OF.log('Successfully bootstrapped');if(isLogin){OF.sendAction('dashboard');}else{OF.sendAction('dismiss');}}else if(status.match(/404/)){OF.alert(_('Account not found'),"该手机号，邮箱或用户名不存在");}else if(status.match(/401/)){OF.alert(_('Incorrect Password'),_('The password entered is incorrect.'));}if(status.match(/8080/)){OF.sendAction("openSpecifyDashboardScreen",OF.page.params);}else{var message=response.exception.message?$.htmlDecode(response.exception.message):_('Check your email address and password and try again');OF.alert(_('Authentication failed'),message);}}
$('#submit').touch(function(){var need_redirect="false";if(OF.page.params.redirect_screen){need_redirect="true"}
if(!$('form').isLoading()){var email=$.trim($('#user_email').val());var password=$('#user_password').val();$('input').blur();if(email.length&&password.length){$('form').isLoading(true);OF.loader.show();$(this).html(_('Loading&hellip;'));OF.sendAction('loginUser',{email:$.trim($('#user_email').val()),password:$('#user_password').val(),callback:'OF.page.boostrapCallback',need_redirect:need_redirect});}else{$('form').isLoading(false);OF.alert(('错误提示'),"请输入您的手机号，邮箱或用户名和密码进行登录")}}});$('form').submit(function(){$('#submit').touch();return false;});$("#new_account").touch(function(){OF.page.params.for_new_account="true";if(OF.page.params.mode){OF.page.params.mode="switch";OF.push("intro/index",{params:OF.page.params});}else{OF.push("intro/index",{params:OF.page.params});}});
