init:
$('body').addClass('textured');$('#header .loading').hide();$('.button').pretouch();if(OF.page.params.mode=='switch'){OF.introSwitch=true;}
if(OF.introSwitch){$('.switch_flow').unhide(0);$('.intro_flow').hide();}
if(OF.page.params.request_from_settings){$("#start_game").html('返回个人设置').touch(function(){OF.goBack({root:true});});}else{$("#start_game").html('去九城游戏中心').touch(function(){$(this).html("正在进入...");OF.navigateToUrl('dashboard/user');});}
$('.intro_flow').show();$('#submit').touch(function(){if(!$('form').isLoading()){var code=$.trim($('#user_mobile_verify_code').val());$('input').blur();if(code.match(/^(\d){6}$/)!=null){$(this).html("正在提交验证码...");OF.api.request('/xp/users/current_user/set_mobile_verify_code',{httpParams:{timeout:100000},method:'POST',params:{mobile_verify_code:code,},success:function(data){OF.loader.hide();OF.alert("提示","验证通过，您的帐号已经成功绑定该手机号码");if(OF.page.params.request_from_settings){OF.navigateToUrl('settings/index');}else{OF.navigateToUrl('dashboard/user');}},failure:function(data,status){$('form').data('isLoading',false);$('#submit').html("提交验证码");OF.loader.hide();OF.alert("错误提示",data.exception.message);}});}else{$('form').isLoading(false);OF.alert("提示","请填写6位数字的验证码");}}});$("#set_mobile").touch(function(){if(OF.page.params.request_from_settings){OF.navigateToUrl('intro/set_mobile');}else{OF.navigateToUrl('intro/setting_user_profile');}});$('form').submit(function(){$('#submit').touch();return false;});
