init:
OF.setActiveFootermenu();OF.setFootermenu();$('body').addClass('textured');$('#subheader').text(_(OF.page.title));if(OF.user.email){$('.current_info').unhide();$('.current_info .value').text(OF.user.email);}
$('#change_email_form').submit(function(){if($(this).isLoading())return false;if($('#change_email_form').isFormFilled(_('Enter your current password and your new email address to continue.'))){$(this).isLoading(true);var newEmail=$('#user_http_basic_credential_attributes_email').val();OF.api.request('/xp/users/'+OF.user.id,{method:'PUT',loader:'#submit',params:{password:$('#password').val(),user:{http_basic_credential_attributes:{email:newEmail}}},success:function(){OF.goBack();OF.alert(_('Success'),_('You have changed your email to: %1',newEmail));},failure:function(data){if(data.exception){if(data.exception['class']=='PlayerNotAuthenticated'){OF.alert(_('Error'),_('Your curent password was not correct.'));}else{return false;}}else{return false;}},complete:function(){$('#change_email_form').isLoading(false);}});}
return false;});$('#submit').touch(function(){$('#change_email_form').submit();});
