init:
OF.page.footer_menu=null;$('body').addClass('textured');$('#subheader').text(_(OF.page.title));$('#subheader').remove();OF.page.init_setting_request=function(){OF.api.request('/xp/users/me',{params:{for_setting_index_page_optimization:'true'},success:function(data){OF.user=data.user;$('#feint_name .subtitle').html(OF.user.name);if(OF.user.email){$('#email .subtitle').text(OF.user.email);$(".has_email").unhide().touch(function(){OF.push("settings/email");});$(".no_email").hide();$(".has_email").hide();}else{if(OF.user.has_set_password==true){$(".no_email .title").html('填写邮箱').touch(function(){OF.push("settings/email");});}else{$(".no_email").touch(function(){OF.push("settings/init_account");});}
$(".no_email").unhide(0);$(".has_email").hide();}
if(OF.user.has_set_password==true){$(".has_set_password").unhide(0);$("#password").touch(function(){OF.push("settings/password");});$("#forgot_password").touch(function(){OF.push("settings/forgot_password");});}
if(OF.user.mobile_binding_status=='0'){$(".no_mobile").unhide().touch(function(){OF.navigateToUrl('intro/set_mobile?request_from_settings=true');});$(".binding_mobile").hide();$(".bind_mobile").hide();}else if(OF.user.mobile_binding_status=='1'){$(".no_mobile").hide();$(".binding_mobile .title").html("手机号["+OF.user.mobile_number+"]未验证");$(".binding_mobile").unhide(0).touch(function(){OF.navigateToUrl('intro/set_mobile_verify_code?request_from_settings=true');});$(".bind_mobile").hide();}else if(OF.user.mobile_binding_status=='2'){$(".no_mobile").hide();$(".binding_mobile").hide();$(".bind_mobile .title").html("手机号["+OF.user.mobile_number+"]已绑定");$(".bind_mobile").unhide(0);}}});}
OF.page.init_setting_request();$('#switch_account').touch(function(){if(!$('#switch_account').data('isLoading')){var req=OF.api.request("/xp/devices/"+OF.device.identifier+"/users",{httpParams:{timeout:100000},success:function(data){OF.users=data.users;if(OF.users.length>1){OF.sendAction("switchAccount",{params:{users:OF.users}});}else{OF.sendAction("introFlow");}},complete:function(){$("#switch_account").data('isLoading',false);}});}});OF.page.enableSubscribe=function(enabled){var button=$('#subscribe');if(enabled){button.children('span').addClass('subscribed');}else{button.children('span').removeClass('subscribed');}};OF.api.request('/xp/news_letter_subscription',{params:{for_setting_index_page_optimization:'true'},success:function(data){OF.page.enableSubscribe(data.news_letter_subscription.is_subscribed);}});$('#subscribe').touch(function(){if(!$('#subscribe').data('isLoading')){$('#subscribe').data('isLoading',true);var button=$('#subscribe');var isSubscribed=button.children('span').hasClass('subscribed');OF.page.enableSubscribe(!isSubscribed);OF.api.request('/xp/news_letter_subscription',{method:isSubscribed?'DELETE':'POST',success:$.noop,complete:function(){$('#subscribe').data('isLoading',false)}});}});$('#logout').touch(function(){if(confirm(_('Are you sure you want to logout from Feint?'))){OF.sendAction('logout');}});$('#feint_name .subtitle').html(OF.user.name);
